mapper.insert:

	SELECT NVL(MAX(sche_id),0)+1
	FROM private_sche

INSERT INTO private_sche(
						sche_id
						, sche_title
						, sche_content
						, sche_regdate
						, coordinate
						, start_date
						, end_date
						, member_id
						, alarm_date
						)
				VALUES (
						#{scheId}
						, #{scheTitle}
						, #{scheContent}
						, sysdate
						, #{coordinate}
						, TO_DATE(#{startDate}, 'YYYY-MM-DD HH24:MI:SS')
						, TO_DATE(#{endDate}, 'YYYY-MM-DD HH24:MI:SS')
						, #{memberId}
						, #{alarmDate}
						)
